int dp5[MAX + 5], dp3[MAX + 3];
int dpp5[MAX + 6], dpp3[MAX + 4];

char buf[MAX + 1];

int main()
{
	dp5[0] = dp3[0] = 1;
	for (int i = 0; i < MAX; ++i)
	{
		for (int j = 1; j <= 5; ++j)
			(dp5[i + j] += dp5[i]) %= mod;
