typedef long long ll;
const int N = 100001;
const ll mod = 100000007;
ll len5[N];
ll len3[N];
ll dp5[N];
ll dp3[N];

void precalc(){
  len5[0]=1;
  len3[0]=1;
  for(int i=1;i<N;i++){
    len5[i]=0;
    len3[i]=0;
    for(int j=1;j<=5;j++){
      if (i-j < 0)continue;
      len5[i]+=len5[i-j];
      len5[i]%=mod;
    }
    for(int j=1;j<=3;j++){
      if (i-j < 0)continue;
      len3[i]+=len3[i-j];
      len3[i]%=mod;
    }
  }

  //O(n)
  dp5[0]=0;
  dp3[0]=0;
  for(int i=1;i<N;i++){
    dp5[i]=len5[i];
    dp3[i]=len3[i];
    if (i-5 > 0){
      dp5[i]+=dp5[i-5];
      dp5[i]%=mod;
    }
    if (i-3 > 0){
      dp3[i]+=dp3[i-3];
      dp3[i]%=mod;
    }
  }

}

main(){
  precalc();
  string in;
  while(cin>>in){
    if (in == "#")break;
    in=in+"*";//endmark
    //8,0 or 1,2,3,4,5,6,7,9
    ll ans=1;
    char prev='+';
    int cnt=1;
    for(int i=0;i<in.size();i++){
      if (prev != in[i]){
	if (prev == '0' || prev == '8')ans=ans*dp3[cnt];
	else if (isdigit(prev))ans=ans*dp5[cnt];
	ans%=mod;
	prev=in[i];
	cnt=1;
      }else cnt++;
    }
    cout << ans%mod << endl;
  }
  return false;
}

