void compute()
{
  vector<ii> vec;
  char cur = message[0];
  int cnt = 0,max_length = 0;
  rep(i,message.size())
    {
      cnt++;
      if((i+1<message.size() && cur != message[i+1]) || i == message.size()-1)
	{
	  vec.push_back(ii(cur-'0',cnt));
	  max_length = max(max_length,cnt);
	  cnt = 0;
	  cur = message[i+1];
	}
    }

  max_length++;
  ull dp[2][max_length];//dp[0] : 3, dp[1] : 5
  rep(i,3)dp[0][i] = (ull)pow(2.0,i);
  rep(i,6)dp[1][i] = (ull)pow(2.0,i);

  REP(i,3,max_length)dp[0][i] = dp[0][i-1] + dp[0][i-2] + dp[0][i-3] + 1;
  REP(i,6,max_length)dp[1][i] = dp[1][i-1] + dp[1][i-2] + dp[1][i-3] + dp[1][i-4] + dp[1][i-5] + 1;

  ull ans = 1;
  rep(i,vec.size())
    {
      int type = ((vec[i].first == 8 || vec[i].first == 0)?0:1);
      ans = ( ans * dp[type][vec[i].second-1] ) % MOD;
    }

  cout << ans << endl;

}

