ll dp[10][100001];

// kindsize
ll dfs(int kind,int size){
    int limit=5;
    if(kind==8||kind==0)limit=3;
    if(size==1)
        return 1;
    if(dp[kind][size]!=INF)
        return dp[kind][size];
    ll sum=1;
    for(int i = 1; i <= min(limit,size-1); i++){
        sum+=(dfs(kind,size-i));
        sum%=MOD;
    }
    return dp[kind][size]=sum;
}

void solve(){
