        int size=0;
        for(int i = 0; i < str.size(); i++){
            if(str[i]!=prv){
                mul*=dfs(prv-'0',size);
                mul%=MOD;
                size=1;
                prv=str[i];
            }
            else if(i==str.size()-1){
                mul*=dfs(prv-'0',size+1);
                mul%=MOD;
            }
            else
                size++;
        }
        cout<<mul%MOD<<endl;
    }
}

