			int num = v[i].first;
			int tp = v[i].second;
			long long int S = 0;
			long long int DP[5] = {1};

			for(int j = 1; j <= num + 1; j++){
				if((num - j) % tp == 0){
					for(int k = 0; k < tp; k++){
						S += DP[k];
						S %= MOD;
					}
				}
				long long int DP2[5] = {};
				for(int k = 0; k < tp; k++){
					DP2[0] += DP[k];
					DP2[0] %= MOD;
				}
				for(int k = 1; k < tp; k++){
					DP2[k] = DP[k - 1];
					DP2[k] %= MOD;
				}
				swap(DP, DP2);
			}
			ans *= S;
			ans %= MOD;
		}

		cout << ans << endl;
	}
	return 0;
}

