const int mod = 100000007;
const int MAX = 100000;
void dpru(int* dp, int period)
{
	static int aperi_dp[MAX + 10];
	fill(aperi_dp, aperi_dp + (MAX + 10), 0);
	// aperi_dp[i] = sum[j = i-period .. i-1](aperi_dp[i - j])
	aperi_dp[0] = 1;
	for (int i = 0; i < MAX; ++i)
		for (int j = 1; j <= period; ++j)
			(aperi_dp[i + j] += aperi_dp[i]) %= mod;

	fill(dp, dp + (MAX + 10), 0);
	// dp[i] = aperi_dp[i] + dp[i - period]
	// dp[0] = 0
	for (int i = 1; i <= MAX; ++i)
	{
		(dp[i] += aperi_dp[i]) %= mod;
		(dp[i + period] += dp[i]) %= mod;
	}
}
int main()
{
	ios::sync_with_stdio(false);

	static int dp3[MAX + 100], dp5[MAX + 100];
	dpru(dp3, 3);
	dpru(dp5, 5);

	for (string s; cin >> s, s[0] != '#'; )
	{
		s += '@';
