ll divide(char prev, int count)
{
    int limit;
    ll ans;

    limit = 5;

    if (prev == '0' || prev == '8'){
        limit = 3;
    }

    ans = 0;

    while (count > 0){
        ans = (ans + dp[limit][count]) % MOD;
        count -= limit;
    }

    return (ans);
}

