		for(;;){
			String s=sc.next();
			if(s.equals("#")){
				break;
			}

			int pm=-1;
			long res=1;
			long[] dp={1}, dp2=null;
			int n=1;
			s+="#";
			int k=0;
			for(char c : s.toCharArray()){
				int m=c-'0';
