					for(int i=0; i<n; i++){
						dp2[i]=dp[i];
					}
					fill(dp, 0);
					for(int i=0; i<n; i++){
						if(i==n-1){
							if(k%n==0)
								dp[0]+=1;
						}else{
							dp[i+1]+=dp2[i];
						}
						dp[0]+=dp2[i];
