    r[$ - 1] = 1;
    for (long i = s.length - 2; i >= 0; i--) {
        if (s[i] == s[i + 1]) {
            r[i] = r[i + 1] + 1;
        } else {
            r[i] = 1;
        }
    }
    long[][] dp = new long[][](2, s.length + 1);
    void initDP(int d) {
        long[] cdp = dp[d == 3];
        long length = s.length;
        for (int i = 1; i <= min(d, length); i++) cdp[i] = 1;
        for (int i = 2; i <= length; i++) {
            for (int j = 1; j <= d; j++) {
                if (i - j >= 1) {
                    cdp[i] += cdp[i - j];
                }
            }
        }
    }
    initDP(3);
    initDP(5);
    long[] cacheF = new long[s.length];
