namespace solution {
    using namespace std;

    typedef istringstream ISS;
    typedef ostringstream OSS;
    typedef vector<string> VS;
    typedef long long LL;
    typedef int INT;
    typedef vector<INT> VI;
    typedef vector<VI> VVI;
    typedef pair<INT, INT> II;

    const int SIZE = 101;
    const int WORD_MAX = 1001;
    int n;
    int m;
    int w;
    int p;
    string S[WORD_MAX];
    int si;
    string T[SIZE][SIZE];
    int pi;
    int ti;

    class Solution {
    private:
        void init() {
        }

        int get_number() {
            string line;
            getline( cin, line );
            ISS iss( line );
            int res;
            iss >> res;
            return res;
        }

        bool input() {
            n = get_number();
            if ( n == 0 ) return false;
            m = get_number();
            w = get_number();
            p = get_number();

            string line;
            si = 0;
            while ( getline( cin, line ) ) {
                if ( line == "?" ) break;
                S[si ++] = line;
            }
            return true;
        }

        void add_item( string s ) {
            int len = s.size();
            if ( len > w ) {
                for ( int i = 0; i < len; ) {
                    string t = s.substr( i, w );
                    add_item(t);
                    i += w;
                }
            } else {
                int r = ti % n;
                int c = ti / n;

                for ( int i = 0; i < len; ++ i ) {
                    T[pi-1][r][i+w*c+p*c] = s[i];
                }

                ti ++;
                if ( ti >= n * m ) {
                    ti = 0;
                    pi ++;
                }
            }
        }

        void solve() {
            pi = 1;
            ti = 0;
            for ( int i = 0; i < SIZE; ++ i ) {
                for ( int j = 0; j < n; ++ j ) {
                    T[i][j] = string( m * w + p * ( m - 1 ), '.' );
                }
            }

            for ( int i = 0; i < si; ++ i ) {
                string word = S[i];
                add_item(word);
            }
        }

        void output() {
            for ( int i = 0; i < pi; ++ i ) {
                for ( int j = 0; j < n; ++ j ) {
                    cout << T[i][j] << endl;
                }
                cout << "#" << endl;
            }
            cout << "?" << endl;
        }

    public:
        int run() {
            while ( init(), input() ) {
                solve();
                output();
            }
            return 0;
        }
    };
}

int main() {
    using namespace solution;
    Solution s;
    return s.run();
}

#include <bits/stdc++.h>

using namespace std;

#define REP(i,a,b) for(int i=a;i<(int)b;i++)
#define rep(i,n) REP(i,0,n)

int PLen, CNum, Width, CSpace;
vector<string> lines;

void solve() {
  vector<string> output;
  int N = PLen, M = Width * CNum + CSpace * (CNum - 1);
  output.resize(PLen);
  rep(i, N) output[i] = string(M, '.');

  int currColumn = 0;
  pair<int, int> currPos = {0, 0};

  auto printPage = [&]() {
    rep(i, N) cout << output[i] << endl;
    cout << "#" << endl;
    fill(output.begin(), output.end(), string(M, '.'));
    currColumn = 0;
    currPos = {0, 0};
  };

  auto print = [&](char c) {
    if(currPos.second >= (currColumn * Width + CSpace * currColumn) + Width) {
      currPos.first ++;
      if(currPos.first >= PLen) {
	currPos.first = 0;
	currColumn ++;
      }
      currPos.second = currColumn * Width + CSpace * currColumn;
    }

    if(currColumn >= CNum) {
      printPage();
    }

    output[currPos.first][currPos.second++] = c;
  };

  auto nextLine = [&]() {
    currPos.first ++;
    if(currPos.first >= PLen) {
      currColumn ++;
      currPos.first = 0;
    }
    currPos.second = currColumn * Width + CSpace * currColumn;
  };

  rep(i, lines.size()) {    
    int LLen = lines[i].size();
    rep(j, LLen) {
      print(lines[i][j]);
    }
    nextLine();
  }

  printPage();
  cout << "?" << endl;
}

int main() {

  while(1) {

    lines.clear();

    cin >> PLen >> CNum >> Width >> CSpace; cin.ignore();
    if(PLen == 0) break;

    for(int i=0;; i++) {
      string in;
      getline(cin, in);
      if(in == "?") break;
      else {
	lines.push_back(in);
      }
    }
    solve();

  }

  return 0;
}

