  string str;
  while(cin >> plen && plen){
    init();
    cin >> cnum >> width >> cspace;

    cin.ignore();
    x = y = 0;
    while(getline(cin,str), str != "?"){
      ss = str;
      if(str.empty()){
        change();
      }else{
        insert(str);
      }
    }
    print();
  }

  return 0;
}

