using namespace std;

int main(int argc, char*argv[])
{
  for(;;) {
    int P, C, W, S;
    cin >> P;
    if(P==0) break;
    cin >> C >> W >> S;
    vector<string> lines;
    string line;
    int n = 0;
    getline(cin, line); // to skip the first one 
    for(;;) {
      getline(cin, line);
      if(line == "?") break;
      lines.push_back(line);
      n++;
    }
    auto printpage=[&](vector<vector<string>> &page) {
      int LS = W*C+(C-1)*S;
      char pp[P][LS+1];
      for(int i = 0; i < P; i++) {
        for(int j = 0; j < LS; j++) {
          pp[i][j] = '.';
        }
        pp[i][LS] = 0;
      }
      for(int c = 0; c < page.size(); c++) {
        for(int s = 0; s < page[c].size(); s++) {
          for(int k = 0; k < page[c][s].size(); k++) {
            pp[s][c*W+c*S + k] = page[c][s][k];
          }
        }
      }
      for(int i = 0; i < P; i++) {
        cout << pp[i] << endl;
      }
      cout << "#" << endl;
    };
    vector<vector<string>> page;
    vector<string> col;
    for(int i = 0; i < n;) {
      if(lines[i].length() > W) {
        col.push_back(lines[i].substr(0, W));
        lines[i] = lines[i].substr(W);
      } else {
        col.push_back(lines[i]);
        i++;
      }
      if(col.size() == P) {
        page.push_back(col);
        col.clear();
      }
      if(page.size() == C) {
        printpage(page);
        page.clear();
      }
    }
    if(col.size() > 0) {
      page.push_back(col);
    }
    if(page.size() > 0) {
      printpage(page);
    }
    cout << "?" << endl;
  }
  return 0;
}

