int main() {
    while (true) {
        int plen, cnum, width, cspace;
        cin >> plen >> cnum >> width >> cspace;
        if (plen == 0) {
            break;
        }
        cin.ignore();
        queue<string> lines;
        string line;
        while (true) {
            getline(cin, line);
            if (line == "?") {
                break;
            } else {
                lines.push(line);
            }
        }
        while (lines.empty() == false) {
            vector<string> outputLines(plen);
            for (int x = 0; x < cnum; x++) {
                for (int y = 0; y < plen; y++) {
                    if (x > 0) {
                        outputLines[y].insert(outputLines[y].end(), cspace, '.');
                    }
                    if (lines.empty() == false) {
                        if ((int) lines.front().length() <= width) {
                            outputLines[y].insert(outputLines[y].end(), lines.front().begin(), lines.front().end());
