                            lines.pop();
                        } else {
                            outputLines[y].insert(outputLines[y].end(), lines.front().begin(), lines.front().begin() + 8);
                            lines.front().erase(lines.front().begin(), lines.front().begin() + 8);
                        }
                    } else {
                        outputLines[y].insert(outputLines[y].end(), width, '.');
                    }
                }
