}

int Idx(int a){
  if(!a)return 0;
  return a*(s+w);
}

void write(string str){
  for(int i=0;i<(int)str.size();i++) mp[I][Idx(J)+i]=str[i];
  I++;

  if(I==p&&J>=c-1){
    for(int i=0;i<p;i++) printf("%s\n",mp[i]);
    cout<<"#"<<endl;
    init();
  }
  if(I==p)I=0,J++;

