  output[currPos.first][currPos.second++] = c;
}

void nextLine() {
  currPos.first ++;
  if(currPos.first >= PLen) {
    if(dirty) printPage();
    currColumn ++;
    currPos.first = 0;
  }
  currPos.second = currColumn * Width + CSpace * currColumn;
}

void solve() {

  output.clear();
  output.resize(PLen);
  dirty = 1;

  N = PLen, M = Width * CNum + CSpace * (CNum - 1);

  rep(i, N) output[i] = string(M, '.');

  currColumn = 0;
  currPos = {0, 0};

  rep(i, lines.size()) {    
    int LLen = lines[i].size();
    dirty = 1;
    rep(j, LLen) {
      print(lines[i][j]);
    }
    nextLine();
  }

  if(dirty) printPage();
  cout << "?" << endl;
}

int main() {

  while(1) {

    lines.clear();    
    cin >> PLen >> CNum >> Width >> CSpace; cin.ignore();

    if(PLen == 0) break;

    for(int i=0;; i++) {
      string in;
      getline(cin, in);
      if(in == "?") break;
      else {
	lines.push_back(in);
      }
    }

    solve();

  }

  return 0;
}

