int main()
{
    while (true) {
        int plen;
        cin >> plen;
        if (plen == 0) {
            break;
        }
        int cnum;
        cin >> cnum;
        int width;
        cin >> width;
        int cspace;
        cin >> cspace;
        vector<string> v;
        {
            string s;
            // ignore newline character after cspace
            getline(cin, s);
            while (true) {
                getline(cin, s);
                if (s == "?") {
                    break;
                }
                v.push_back(s);
            };
        }
        vector<string> columnLines;
        for (auto&& i : v) {
            if (i == "\n") {
                columnLines.emplace_back(width, '.');
            }
            else if (i.size() < width) {
                columnLines.push_back(i + string(width - i.size(), '.'));
            }
            else if (i.size() == width) {
                columnLines.push_back(i);
            }
            else {
                auto t = i;
                while (true) {
                    columnLines.push_back(
                        t.substr(0, static_cast<size_t>(width)));
                    t = t.substr(static_cast<size_t>(width));
                    if (t.size() < width) {
                        break;
                    }
                }
                if (!t.empty()) {
                    columnLines.push_back(t + string(width - t.size(), '.'));
                }
            }
        }
        auto emptyLines = columnLines.size() % (plen * cnum);
        if (emptyLines != 0) {
            auto s = string(static_cast<size_t>(width), '.');
            fill_n(back_inserter(columnLines), plen * cnum - emptyLines, s);
        }

        vector<vector<string>> pages;
        for (int i = 0; i < columnLines.size() / (plen * cnum); ++i) {
            vector<string> p;
            copy_n(begin(columnLines) + i * plen * cnum, plen * cnum,
                   back_inserter(p));
            pages.push_back(move(p));
        }

        for (auto&& page : pages) {
            for (int i = 0; i < plen; ++i) {
                stringstream ss;
                for (int j = 0; j < cnum; ++j) {
                    if (j != 0) {
                        ss << string(static_cast<size_t>(cspace), '.');
                    }
                    ss << page[i + j * plen];
                }
                cout << ss.str() << endl;
            }
            cout << '#' << endl;
        }

        cout << "?" << endl;
    }
}

