const int INF = 1<<29;

string buf[101];

int main() {
  string input;
  vector<string> vs;
  int plen,cnum,width,cspace;
  while(cin>>plen, plen) {
    cin>>cnum>>width>>cspace;
    cin.ignore();
    vs.clear();
    while(true) {
      getline(cin, input);
      if(input == "?") break;
      vs.push_back(input);
    }

    vector<string> normalized;
    for(int i=0; i<vs.size(); ++i) {
      while(vs[i].length() > width) {
        normalized.push_back(vs[i].substr(0,width));
        vs[i] = vs[i].substr(width);
      }
      normalized.push_back(vs[i]);
    }

    for(int i=0; i<normalized.size(); ++i) {
      if(normalized[i].length() < width)
        normalized[i] += string(width-normalized[i].length(), '.');
    }

    if(normalized.size()%(plen*cnum) > 0) {
      for(int i=0; i<normalized.size()%(plen*cnum); ++i) {
        normalized.push_back(string(width, '.'));
      }
    }

    vector<vector<string> > pages;
    for(int i=0; i<normalized.size(); i += plen * cnum) {
      vector<string> op;
      for(int j=0; j<plen*cnum; ++j)
        op.push_back(normalized[i+j]);
      pages.push_back(op);
    }

    for(int i=0; i<pages.size(); ++i) {
      for(int j=0; j<100; ++j) buf[j] = "";
      for(int j=0; j<pages[i].size(); ++j) {
        buf[j%plen] += (j>=plen?".":"") + pages[i][j];
      }
      for(int i=0; i<plen; ++i) cout<<buf[i]<<endl;
      cout<<"#"<<endl;
    }
    cout<<"?"<<endl;
  }
}

