	while(cin >> plen && plen){
		cin >> clen >> width >> cspace;
		string s;
		getline(cin,s);
		vector<string> ad;
		while( getline(cin,s) && s[0] != '?' ){
			if( s.size() && s[s.size()-1] == '\r' ) s = s.substr(0,s.size()-1);
			if(s=="")ad.push_back("");
			while(s.size()){
				ad.push_back(s.substr(0,min<int>(width,s.size())));

				s = s.substr(min<int>(width,s.size()));
			}
		}
		vector< vector< vector<string> > > output( (ad.size()+plen*clen-1) / (plen*clen) , vector< vector<string> > (plen,vector<string>(clen)));
		for(int i = 0 ; i < ad.size() ; i++){
			int p = i/(plen*clen);
			int x = i / plen % clen;
			int y = i % plen;
			output[p][y][x] = ad[i];
		}
		for(int i = 0 ; i < output.size() ; i++){
			for(int j = 0 ; j < output[i].size() ; j++){
				for(int k = 0 ; k < output[i][j].size() ; k++) cout << (k?string(cspace,'.'):"") << fix(output[i][j][k]);
				cout << endl;
			}
