using System;

public class hello
{
    public static string[] dot;
    public static int plen, cnum, width, cspace;
    public static void Main()
    {
        while (true)
        {
            plen = int.Parse(Console.ReadLine().Trim());
            if (plen == 0) break;
            cnum = int.Parse(Console.ReadLine().Trim());
            width = int.Parse(Console.ReadLine().Trim());
            cspace = int.Parse(Console.ReadLine().Trim());
            dot = new string[width + 1];
            for (int i = 0; i <= width; i++) dot[i] = new string('.', i);
            var text = new List<string>();
            while (true)
            {
                var s = Console.ReadLine().Trim();
                if (s == "?") break;
                addText(text, s);
            }
            addDot(text);
            print(text);
        }
    }
    static void print(List<string> t)
    {
        var pc = plen * cnum;
        var imax = t.Count() / (pc);
        var line = new string[cnum];
        for (int i = 0; i < imax; i++)
        {
            for (int j = 0; j < plen; j++)
            {
                for (int k = 0; k < cnum; k++)
                {
                    var p = i * pc + j + k * plen;
                    line[k] = t[p];
                }
                Console.WriteLine(string.Join(".", line));
            }
            Console.WriteLine("#\n?");
        }
    }
    static void addDot(List<string> t)
    {
        var pg = (t.Count() + plen * cnum - 1) / (plen * cnum);
        var adddot = pg * plen * cnum - t.Count();
        for (int i = 0; i < adddot; i++) t.Add(dot[width]);
    }
    static void addText(List<string> t, string s)
    {
        if (s == "") { t.Add(dot[width]); return; }
        var imax = (s.Length + width - 1) / width;
        var p = 0;
        for (int i = 0; i < imax; i++)
        {
            if (i == imax - 1)
            {
                var ss = s.Substring(p);
                t.Add(ss + dot[width - ss.Length]);
            }
            else
            {
                var ss = s.Substring(p, width);
                t.Add(ss);
                p += width;
            }
        }
    }
}

