void dfs(vector<int> v,int k){
    if (k==num.size()){
        int sum=accumulate(v.begin(),v.end(),0);
        if (t<sum) return;
        else if (Max<sum){
            Max=sum,ng=0;
            ans=v;
        } else if (Max==sum) ng=1;
        return;
    }
    if (!v.empty()){
        v.back()=v.back()*10+(num[k]-'0');
        dfs(v,k+1);
        v.back()/=10;
    }
    v.emplace_back(num[k]-'0');
    dfs(v,k+1);
}

