		rep(i, (1 << (digit - 1))) {
			int temp = s;
			vector<int> splits;
			int count = 0;

			rep(j, digit - 1) {
				if ((i >> j) % 2 == 1) {
					splits.push_back(temp%mypow(10, count + 1));
					temp /= mypow(10, count + 1);
					count = 0;
				}
				else {
					count++;
				}
			}

			splits.push_back(temp);

			int split_sum = vec_sum(splits);

			if (split_sum > t) {
				continue;
			}

			if (t - split_sum < dif) {
				dif = t - split_sum;
				ans = split_sum;
				ans_splits = splits;
				errorflag = false;
				rejectflag = false;
			}
			else if (t - split_sum == dif) {
				rejectflag = true;
			}
		}

