			cout << "error" << endl;
		}
		else if (rejectflag == true) {
			cout << "rejected" << endl;
		}
		else {
			cout << ans;

			for (int i = ans_splits.size() - 1; i >= 0; i--) {
				cout << " " << ans_splits[i];
			}

			cout << endl;
		}

	}

