  else return dig(n/10)+1;
}

int main() {
  while(1){
    int t,n;
    cin>>t>>n;
    if(!n)break;
    t=min(t,n);
    int s=dig(n);
    string ns=to_string(n);
    vector<vector<int>> dp(s+1,vector<int>(n+1));
    dp[0][0] = 1;
    REP(i,s+1){
      REP(j,i){
        auto sub=ns.substr(j,i-j);
        int m=stoi(sub);
        REP(k,n-m+1){
          if (dp[i][k+m] == 0 && dp[j][k] > 0)
            dp[i][k+m] = j+1;
          else if ((dp[i][k+m] != 0 && dp[j][k] != 0) || dp[j][k] < 0)
            dp[i][k+m] = -1;
        }
      }
    }
    bool ok=false;
    for (int u=t;u>0;--u) {
      if (dp[s][u] > 0) {
        vector<int> res;
        int i=s;
        int j=u;
        do {
          int oldi=i;
          i=dp[i][j]-1;
