int toInt(string s)
{
	int res = 0;
	for(int i=0; i<s.size(); i++) {
		res *= 10;

		res += s[i]-'0';
	}

	return res;
}

int res,cnt;
vector<int> pat;

void solve(int T, int n, int s, vector<int>& a, string seq)
{
	if(n == seq.size()) {
		if(s == res) cnt++;
		else if(s > res) {
			cnt = 0;
			res = s;
			pat = a;
		}
	}

	for(int i=1; i<=6; i++) {
		if(seq.size() - n < i) continue;
		int num = toInt(seq.substr(n, i));
		int sum = s + num;

		if(sum > T) continue;

		a.push_back(num);
		solve(T, n+i, sum, a, seq);
		a.pop_back();
	}
}

int main()
{
	int T;
	string N;
	while(cin >> T >> N, (T||N!="0")) {
		res = -1;
		cnt = 0;
		vector<int> a;

		solve(T,0,0,a,N);

		if(res == -1) cout << "error" << endl;
		else if(cnt >= 1) cout << "rejected" << endl;
		else { 
			cout << res;
			for(int i=0; i<pat.size(); i++)
				cout << " " << pat[i];

			cout << endl;
		}
	}

}

