using namespace std;

vector<int> calc(int t, string &num, int key){
	vector<int> sum;
	string::reverse_iterator it = num.rbegin();
	int temp = *it - '0';
	int mlt = 10;
	it++;
	for(int i = 1; i <= 1 << (num.size() -2); i <<= 1){
		if((key & i) == i){
			sum.push_back(temp);
			temp = *it - '0';
			mlt = 10;
		}
		else{
			temp += (*it - '0')*mlt;
			mlt *= 10;
		}
		it++;
	}
	sum.push_back(temp);
	/*
	for(int i = 0; i < sum.size(); i++){
		cout << sum[i] << " ";
	}
	cout << endl;
	*/
	return sum;
}

int main(){
	while(true){
		int t;
		string num;
		cin >> t >> num;
		if(t == 0 )break;
		vector<int> ans(1);
		ans[0] = atoi(num.c_str());
		int anssum = t >= ans[0] ? ans[0] : 0;
		bool flag = false;
		for(int i = 1; i < (1 << (num.size()-1)); i++){
			vector<int> temp = calc(t,num,i);
			int sum = accumulate(temp.begin(),temp.end(),0);
			if(sum > t)continue;
			if(sum > anssum){
				anssum = sum;
				swap(ans,temp);
				flag = false;
			}
			else if(sum == anssum){
				flag = true;
			}
		}
		if(anssum == 0){
			cout << "error" << endl;
		}
		else if(flag){
			cout << "rejected" << endl;
		}
		else {
			cout << anssum << " ";
			for(int i = ans.size()-1; i >= 0; i--){
				cout << ans[i];
				if(i != 0){
					cout << " ";
				}
			}
			cout << endl;
		}
	}

	return 0;
}

