using namespace std;

vector<int> res;
int r;
bool rej;

void func(vector<int> vec, string str, int t, int s)
{
	if(str.size() == 0){
		if(s > t){
			return ;
		} else if(r == s){
			rej = true;
		} else if(r < s){
			rej = false;
			r = s;
			res = vec;
		}
	} else {
		string s1 = "", s2;
		for(int i = 0; i < str.size(); i++){
			s1 += str[i];
			if(i+1 < str.size()){
				s2 = str.substr(i+1);
			} else {
				s2 = "";
			}
			int v = atoi(s1.c_str());
			vector<int> v2 = vec;
			v2.push_back(v);
			func(v2, s2, t, s+v);
		}
	}

	return ;
}

int main()
{
	int t;
	string str;

	while(cin>>t>>str && !(t == 0 && str == "0")){
		vector<int> vec;
		rej = false;
		r = -1;
		func(vec, str, t, 0);
		if(res.size() == 0){
			cout << "error" << endl;
		} else {
			if(rej){
				cout << "rejected" << endl;
			} else {
				cout << r;
				for(int i = 0; i < res.size(); i++){
					cout << " " << res[i];
				}
				cout << endl;
			}
		}

		res.clear();
	}

	return 0;
}

