string solve(int t, string& num) {
    int n = num.size();

    string ans = "error";
    int max_v = 0;
    for (int j = 0; j < (1 << n); j += 2) {
        stringstream ss, tt;
        int sum = 0, a = num[0] - '0';
        for (int k = 1; k < n; ++k) {
            if ((j >> k) & 1 != 0) {
                a = 10 * a + (num[k] - '0');
            } else {
                sum += a;
                ss << a << ' ';
                a = num[k] - '0';
            }
        }
        ss << a;
        sum += a;
        if (max_v <= sum && sum <= t) {
            if (sum == max_v) {
                ans = "rejected"; 
            } else {
                max_v = sum;
                tt << sum << " " << ss.str();
                ans = tt.str();
            }
        }
    }
    return ans;
}

int main() {
    int t, n;
    string num;
    while (true) {
        cin >> t >> num;
        if (t == 0 && num == "0") {
            break;
        }
        cout << solve(t, num) << endl;
    }
    return 0;
}

