#include <bits/stdc++.h>
using namespace std;

int target;
string s;
int maxi;
vector<int> ans, parts;

void rec(int sum, int p) {
  if(sum > target) {
    return;
  }
  if(p == s.size()) {
    if(sum == maxi) {
      ans.clear();
    } else if(sum > maxi) {
      ans = parts;
      maxi = sum;
    }
    return;
  }
  for(int len = 1; p+len <= s.size(); ++len) {
    int part = atoi(s.substr(p, len).c_str());
    parts.push_back(part);
    rec(sum + part, p+len);
    parts.pop_back();
  }
}

