  while(cin >> target >> s && !(target == 0 && s == "0")) {
    maxi = -1;
    rec(0, 0);
    if(maxi == -1) cout << "error" << endl;
    else if(ans.empty()) cout << "rejected" << endl;
    else {
      cout << maxi;
      for(int i = 0; i < ans.size(); ++i) {
        cout << " " << ans[i];
      }
