int sum(string n) {
    int res = 0;
    for (char c : n) res += (c - '0');
    return res;
}
int sum(const vector<int> &v) {
    int res = 0;
    for (int e : v) res += e;
    return res;
}

int anssum = 0;
vector<int> ans;
bool rejected = false;
void dfs(const int &n, const string &m, int idx, vector<int> nowvec) {
    if (idx == m.length()) {
        const int vsum = sum(nowvec);
        if (vsum <= n && abs(vsum - n) < abs(n - anssum)) {
            anssum = vsum;
            ans = nowvec;
            return;
        }
    }
    for (int i = idx + 1; i <= m.length(); i++) {
        int buf = 0;
        for (int j = idx; j < i; j++) {
            buf = buf * 10 + m[j] - '0';
        }
        nowvec.emplace_back(buf);
        dfs(n, m, i, nowvec);
        nowvec.pop_back();
    }
    return;
}
void dfs2(const int &n, const string &m, int idx, vector<int> nowvec) {
    if (idx == m.length()) {
        const int vsum = sum(nowvec);
        rejected |= ((vsum == anssum) && (ans != nowvec));
    }
    for (int i = idx + 1; i <= m.length(); i++) {
        int buf = 0;
        for (int j = idx; j < i; j++) {
            buf = buf * 10 + m[j] - '0';
        }
        nowvec.emplace_back(buf);
        dfs2(n, m, i, nowvec);
        nowvec.pop_back();
    }
    return;
}

void solve(int n, string m) {
    if (n < sum(m)) {
        puts("error");
        return;
    }
    if (stoi(m) == n) {
        printf("%d %d\n", n, n);
        return;
    }
    dfs(n, m, 0, vector<int>());
    dfs2(n, m, 0, vector<int>());
    if (rejected) {
        puts("rejected");
        return;
    }
    printf("%d ", accumulate(begin(ans), end(ans), 0));
    for (int i = 0; i < ans.size(); i++) {
        printf("%d%c", ans[i], (i + 1 == ans.size() ? '\n' : ' '));
    }

}

void refresh() {
    rejected = false;
    anssum = 0;
    ans.clear();
}

void last() {
    exit(0);
}

int main(void) {
	int a, b, c, x, y, z, n, m, p, k;
	string s;
	while (cin >> n >> s, n) {
		solve(n, s);
        refresh();
	}
    last();
}

