#define REP(i, n) for ( int i = 0; i < n; i++ )
#define NIL -1

using namespace std;

int N, maxVal;
vector<vector<int> > ans;

int getFigure(int num) {
    if(num == 0) return 1;

    int ret = 0;
    while(num > 0) {
        num /= 10;
        ret++;
    }
    return ret;
}

void merge(vector<int> &nums, int idx) {
    int nonZero = 0,
        prevIdx = 0;
    REP(i, nums.size()) {
        if(nums[i] == NIL) continue;
        nonZero++;

        if(nonZero == idx + 2 && nums[prevIdx] != 0) {
            int step = getFigure(nums[i]);
            REP(j, step) nums[prevIdx] *= 10;
            nums[prevIdx] += nums[i];
            nums[i] = NIL;
            break;
        }
        prevIdx = i;
    }
}

int sum(vector<int> &nums) {
    int ret = 0;
    REP(i, nums.size()) if(nums[i]!=NIL) ret += nums[i];
    return ret;
}

void dump(vector<int> &nums) {
    REP(i, nums.size()) cout << nums[i] << ",";
    // REP(i, nums.size()) if(nums[i]!=NIL)cout << nums[i] << ",";
    cout << endl;
}

bool equal(vector<int> &a, vector<int> &b) {
    int ai = 0, bi = 0;
    REP(i, a.size()) {
        while(a[ai] == 0 && ai < a.size()) ai++;
        while(b[bi] == 0 && bi < b.size()) bi++;
        if(a[ai] != b[bi]) return 0;
        ai++; bi++;
    }
    return 1;
}

bool valid(vector<int> &nums) {
    int cnt = 0;
    REP(i, nums.size()) if(nums[i] != NIL) cnt += getFigure(nums[i]);
    return cnt == nums.size();
}

void solve(vector<int> &nums, int d = 0, int oft = 0) {
    // cout << d << ": ";
    // dump(nums);

    int s = sum(nums);
    vector<int> base = nums;

    if(s > N) return;

    if (s > maxVal) {
        ans.clear();
        ans.push_back(nums);

        maxVal = s;

    } else if(s == maxVal && !equal(ans[0], nums)) {
        ans.push_back(nums);
    }

    for(int i = oft; i < nums.size() - (d+1); i++) {
    // REP(i, nums.size() - (d+1)) {
        merge(nums, i);
        solve(nums, d+1, oft);
        nums = base;
    }
}

int main() {
    int num;
    vector<int> nums;

    while(cin >> N >> num, N||num) {

        maxVal = -1;
        ans.clear();
        nums.clear();

        while(num > 0) {
            nums.push_back(num % 10);
            num /= 10;
        }
        reverse(nums.begin(), nums.end());

        solve(nums);

        // cout << ans.size() << endl;

        // NOTE: どこかの初期化をミスっててとある入力の組み合わせだと同じ答えが入ってしまう
        int dup = 0;
        int containZero = 0;
        if(ans.size() > 1) {
            REP(i, ans.size()-1) {
                if(equal(ans[i], ans[i+1])) dup++;
            }
        } else {
            REP(i, ans[0].size()) {
                if(ans[0][i] == 0) containZero = 1;
            }
        }

        if(ans.size() - dup == 0) {
            cout << "error" << endl;
        } else if (ans.size() - dup >= 2 || containZero) {
            cout << "rejected" << endl;
        } else {
            cout << sum(ans[0]);
            REP(i, ans[0].size()) if(ans[0][i] != NIL) cout << " " << ans[0][i];
            cout << endl;
        }
    }

    return 0;
}

