int main(){
    int t;
    string num;
    while(cin>>t>>num,t!=0 || num!="0"){
        vector<vector<int>> dp(num.size()+1,vector<int>(t+1,0));
        dp[0][0]=1;
        for(int i=0;i<num.size();i++){
            for(int j=0;j<=t;j++){
                for(int k=i+1;k<=num.size();k++){
                    //cut num[i,k)
                    int cutnum=stoi(num.substr(i,k-i));
                    if(cutnum+j<=t) dp[k][j+cutnum]+=dp[i][j];
                }
            }
        }
        for(int i=t;i>=-1;i--){
            if(i==-1){
                cout<<"error"<<endl;
                break;
            }
            if(dp[num.size()][i]){
                if(dp[num.size()][i]>=2){
                    cout<<"rejected"<<endl;
                }
                else{
                    vector<string> res;
                    int pos=num.size();
                    int val=i;
                    while(pos>0){
                        for(int j=0;j<pos;j++){
                            //cut num[j,pos)
                            int v=stoi(num.substr(j,pos-j));
                            if(val-v>=0 && dp[j][val-v]==1){
                                res.push_back(num.substr(j,pos-j));
                                pos=j;
                                val-=v;
                                break;
                            }
                        }
                    }
                    cout<<i<<" ";
                    reverse(res.begin(),res.end());
                    for(int j=0;j<res.size();j++){
                        cout<<res[j] <<(j+1==res.size() ? "\n" : " ");
                    }
                }
                break;    
            }
        }

    }

    return 0;
}

