using namespace std;

#define max 6

int main(void){
	int div[] = {1, 10, 100, 1000, 10000, 100000, 1000000 };

	while(1) {
		int t, num, lim, indx = -1, summax = 0;
		bool error = true, rejected = false;
		stringstream ss;
		vector< vector< int > > vs;

		cin >> t >> num;
		ss << num;
		if( !( t | num ) ) break;

		lim = ( 1 << ( ss.str().length() - 1 ) ) - 1;

		for(int i = 0; i <= lim; i++) {
			vector< int > v;
			int temp = 0;
			for(int j = 0; j < max; j++) {
				if( i & ( 1 << j ) ) {
					v.push_back( ( num % div[j + 1] ) / div[temp] );
					temp = j + 1;
				}
			}
			v.push_back( num / div[temp] );
			vs.push_back( v );
		}

		for(int i = 0; i < vs.size(); i++) {
			int sum = 0;
			for(int j = 0; j < vs[i].size(); j++)
				sum += vs[i][j];
			if( sum <= t ) {
				error = false;
				if( sum == summax ) {
					rejected = true;
				} else if( sum > summax ) {
					indx = i;
					summax = sum;
					rejected = false;
				}
			}
		}
		if( error == true ) {
			cout << "error" << endl;
		} else if( rejected == true ) {
			cout << "rejected" << endl;
		} else {
			cout << summax;
			for(int i = vs[indx].size() - 1; i >= 0; i--)
				cout << " " << vs[indx][i];
			cout << endl;
		}
	}

	return 0;
}

