using namespace std;
typedef pair<int, vector<string> > P;

int t;
string s;

P f(string s, vector<bool> cut) {
	P ret;
	string a;
	a += s[0];
	for(int i = 0; i < cut.size(); i++) {
		if(cut[i]) {
			ret.second.push_back(a);
			a = s[i + 1];
		}
		else {
			a += s[i + 1];
		}
	}
	ret.second.push_back(a);
	for(int i = 0; i < ret.second.size(); i++) {
		if(ret.second[i].size() != 1 && ret.second[i][0] == '0') {
			ret.first = t + 1;
			break;
		}
		else {
			ret.first += atoi(ret.second[i].c_str());
		}
	}
	return ret;
}

vector<vector<string> > ans[1000000];

int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);


	while(cin >> t >> s, t && s != "0") {
		for(int i = 0; i < 1000000; i++) {
			ans[i].clear();
		}
		int n = s.size();
		int m = -1;
		for(int bit = 0; bit < 1 << (n - 1); bit++) {
			vector<bool> cut(n - 1);
			for(int i = 0; i < n - 1; i++) {
				if(bit >> i & 1) cut[i] = true;
			}
			P p = f(s, cut);
			if(p.first > t) continue;
			m = max(m, p.first);
			ans[p.first].push_back(p.second);
		}
		if(m == -1) {
			cout << "error" << endl;
		}
		else if(ans[m].size() >= 2) {
			cout << "rejected" << endl;
		}
		else {
			cout << m;
			for(string v : ans[m][0]) {
				cout << " " << v;
			}
			cout << endl;
		}
	}
}

