        int b = n % d;
        //printf("n=%d d=%d a=%d b=%d\n",n,d,a,b);
        vector<int> _v = v;
        _v.push_back(a);
        d /= 10;
        dfs(_v, b, d);
        if (d == 0) break;
    }
}

