#define TIMES(i, n) for (auto TIMES_n = (n), i = 0 * TIMES_n; i < TIMES_n; i++)
#define RANGE(c) (c).begin(), (c).end()

template <typename T>
std::string to_s(const T &t) {
  std::ostringstream ss;
  ss << t;
  return ss.str();
}

typedef std::vector<int> Ints;

struct Problem {
  int target, num;

  bool read() {
    return scanf("%d %d", &target, &num) == 2 && target != 0;
  }

  void solve() {
    std::string chars = to_s(num);
    int max = -1;
    int lesser = -1;
    Ints max_digits;

    TIMES(n, 1 << (chars.size() - 1)) {
      Ints digits = splits_at(chars, n);
      int sum = std::accumulate(RANGE(digits), 0);
      if (max <= sum && sum <= target) {
        lesser = max;
        max = sum;
        max_digits = std::move(digits);
      }
    }

    if (max < 0) {
      puts("error");
    } else if (max == lesser) {
      puts("rejected");
    } else {
      std::cout << max;
      for (auto n : max_digits) { std::cout << " " << n; }
      std::cout << "\n";
    }
  }

  Ints splits_at(const std::string &str, int n) {
    int i = 0;
    Ints chunks;
    TIMES(j, str.size() - 1) {
      int m = n % 2;
      n /= 2;
      if (m == 0) { continue; }
      chunks.push_back(atoi(str.substr(i, j - i + 1).c_str()));
      i = j + 1;
    }
    chunks.push_back(atoi(str.substr(i).c_str()));

    return chunks;
  }
};

int main() {
  Problem p;
  while (p.read()) { p.solve(); }
}

