int calc_sum(vector<int> &vec){
  int res=0;
  for(int i=0;i<(int)vec.size();i++)res+=vec[i];
  return res;
}

int change(int p){
  return str[p]-'0';
}

void rec(int p,int num){
  num=num*10+change(p);

  if(p+1==len){
    t.push_back(num);
