#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int size, valc, n;
string str;
vector<int> ans, path;
bool reject;

void init(){
  reject = false;
  ans.clear();
  path.clear();
  valc = -1;
  size = str.size();
}

void solve(int p){
  if(p == size){
    int sum = 0;
    for(int i=0;i<path.size();i++) sum += path[i];
    if(sum == valc){
      reject = true;
    }else if(valc < sum && sum <= n){
      valc = sum;
      ans = path;
      reject = false;
    }
  }else{
    for(int i=1;i+p<=size;i++){
      int tmp = atoi((str.substr(p, i)).c_str());
      path.push_back(tmp);
      solve(p+i);
      vector<int>::iterator ite = path.end();
      path.erase(--ite);
    }
  }
}

main(){
  while(cin >> n >> str){
    if(n == 0 && str == "0") break;
    init();
    solve(0);
    if(valc == -1) cout << "error" << endl;
    else if(reject) cout << "rejected" << endl;
    else{
      cout << valc;
      for(int i=0;i<ans.size();i++){
        cout << ' ' << ans[i];
      }
      cout << endl;
    }
  }
}

