typedef pair<int,int> P;

int stoi(string s){
  stringstream ss(s);
  int res; ss>>res;
  return res;
}

int main(){
  int n,ans,mul;
  vector<int> list;
  string m;

  while(cin >> n >> m,n||(m!="0")){
    int l = m.size()-1;
    ans = -1; mul = 0;
    for(int i=0;i<(1<<l);i++){
      vector<P> v; int prv = 0;
      for(int j=0;j<l;j++){
	if((i>>j)&1){
	  v.push_back(P(prv,j)); prv = j+1;
	}
      }
      v.push_back(P(prv,l));

      int sum = 0;
      for(int j=0;j<v.size();j++)
	sum += stoi(m.substr(v[j].first,v[j].second-v[j].first+1));

      if(sum>n)continue;
      if(sum == ans)mul++;
      else if(sum > ans){
	ans = sum; mul = 0;
	list.clear();
	for(int j=0;j<v.size();j++)
	  list.push_back(stoi(m.substr(v[j].first,v[j].second-v[j].first+1)));
      }
    }
    if(ans<0)cout << "error\n";
    else if(mul)cout << "rejected\n";
    else{
      cout << ans;
      for(int i=0;i<list.size();i++)cout << " " << list[i];
      cout << endl;
    }
  }
}

