int dp[1010101];
void dfs(int current,int amari, int cnt){
  if(current == num.size() && cnt == 0){
    dp[amari]++;
    return;
  }

  // 区切る場合
  int mm = cnt*10 + (num[current]-'0');
  if(amari - mm >= 0) dfs(current+1, amari-mm, 0);
  else return;

  // 区切らない場合
  if(current+1 < num.size()) dfs(current+1, amari, mm);

}

VI ansDFS(int current, int amari, int cnt, VI v){
  VI notAns;
  if(current == num.size()){
    if(ans == amari && cnt == -1) {
      return v;
    }
    else return notAns;
  }
  cnt = max(0,cnt);
  VI ansV0;
  VI ansV1;

  int mm = cnt*10 + (num[current]-'0');
  if(amari - mm < 0) return notAns;

  // 区切らない場合
  if(not(current > 0 && num[current] == '0' && num[current-1] == '0')) ansV0 = ansDFS(current+1, amari, mm, v);

  // 区切る場合
  v.push_back(mm);
  // cout << mm << endl;
  ansV1 = ansDFS(current+1, amari-mm, -1,v);

  if(ansV0.size() > 0) return ansV0;
  else return ansV1;
}


