					now.insert(now.end(), j.second.first.begin(), j.second.first.end());
					ans[stoi(from) + j.first] = make_pair(now,1 );
				}
				else {
					ans[stoi(from) + j.first].second++;
				}
			}
		}
	}

	return ans;
}

