        auto a = solve(t,n);
        if(a.first!=-1){
            cout << a.first << " ";
            for(int i=0;i<(int)a.second.size();i++){
                cout << a.second[i] << (i+1==a.second.size() ? "\n" : " ");
            }
        }else{
            cout << a.second[0] << endl;
        }
    }
}

