template<class Type>
Type solve(Type res = Type()){
    ll t;
    string num;
    while(cin >> t >> num,t){
        map<ll,int> mp;

        function<void(int,int,int)> rec1 = [&](int n,int tsum,int sum){
            if(tsum + sum > t) return;
            if(n == num.length()){
                sum += tsum;
                mp[sum]++;
                return;
            }
            rec1(n+1,tsum*10 + (num[n]-'0'),sum);
            rec1(n+1,(num[n]-'0'),sum+tsum);
        };
        rec1(1,num[0]-'0',0);

        if(mp.empty()){
            cout << "error" << endl;
            continue;
        }
        if(mp.rbegin()->second >= 2){
            cout << "rejected" << endl;
            continue;
        }


        vector<int> ans;
        function<bool(int,int,int)> rec2 = [&](int n,int tsum,int sum){
            if(tsum + sum > t) return false;
            if(n == num.length()){
                if(sum+tsum == mp.rbegin()->first){
                    ans.push_back(tsum);
                    return true;
                }
                return false;
            }
            if(rec2(n+1,tsum*10 + (num[n]-'0'),sum)){
                return true;
            }
            if(rec2(n+1,(num[n]-'0'),sum+tsum)){
                ans.push_back(tsum);
                return true;
            }
            return false;
        };
        rec2(1,num[0]-'0',0);

        ans.push_back(mp.rbegin()->first);
        reverse(ans.begin(), ans.end());
        cout << ans << endl;
    }
    return res;
}


