typedef long long ll;
typedef pair<int, int> pii;

int target;
string nums;
int n;

int sum;
vector<int> part;
bool reject;

void rec(int i, vector<int> d, int sums, int tmp) {
	//if (i != 0 && tmp == 0) return;

	if (i == n) {
		sums += tmp;
		d.push_back(tmp);
		/*
		cout << sums << " ";
		for_(k,0,(int)d.size()) {
			if (k < (int)d.size() - 1) cout << d[k] << " ";
			else cout << d[k] << endl;
		}
		*/
		if (target >= sums) {
			if (sums > sum) {
				reject = false;
				sum = sums;
				part = d;
			} else if (sums == sum) {
				bool flag = true;
				int psize = (int)part.size();
				int dsize = (int)d.size();
				if (psize == dsize) {
					vector<int> sp = part, sd = part;
					sort(allof(sp)); sort(allof(sd));
					for_(k,0,psize) if (sp[k] != sd[k]) flag = false;
				}
				if (flag) reject = true;
			}
		}
		return;
	}

	int rtmp = tmp;

	int add = (int)(nums[i] - '0');
	tmp = 10 * tmp + add;
	rec(i + 1, d, sums, tmp);

	if (i != 0) {
		tmp = rtmp;
		sums += tmp;
		d.push_back(tmp);
		tmp = add;
		rec(i + 1, d, sums, tmp);
	}
}

void solve() {
	//cout << target << " " << nums << endl;
	n = (int)nums.size();
	sum = -1;
	part.clear();
	reject = false;

	rec(0,vector<int>(), 0, 0);

	if (sum == -1) cout << "error" << endl;
	else if (reject) cout << "rejected" << endl;
	else {
		cout << sum << " ";
		int psize = (int)part.size();
		for_(i,0,psize) {
			if (i < psize - 1) cout << part[i] << " ";
			else cout << part[i] << endl;
		}
	}
}

int main() {
	while (cin >> target >> nums, target) {
		solve();
	}
	return 0;
}

