using namespace std;

int comb(int n, int k, int target, string num, set< vector<string> > &parts){
  int *p;
  bool loop = false;
  int min_diff = target + 1;

  p = new int[k];
  for(int i = 0; i < k; i++) p[i] = i;

  do{ loop = false;

    // TODO
    int sum = 0;
    int st = 0;
    string s;
    vector<string> vp;
    for(int i = 0; i < k; ++i){
      //cout << num.substr(st,p[i]-st+1) << '@';
      s = num.substr(st,p[i]-st+1);
      if(s.length()>0)
	vp.push_back(s);
      st = p[i]+1;
    }
    if(st<n){
      s = num.substr(st,n-st+1);
      if(s.length()>0)vp.push_back(s);
    }

    for(int i = 0; i < (int)vp.size(); ++i){
      sum += atoi(vp[i].c_str());
      //cout << vp[i] << '@';
    }
    //cout << endl;
    //cout << sum << endl;
    if( target - sum >= 0 && min_diff > target - sum ){
      min_diff = target - sum;
      parts.clear();
      parts.insert(vp);
    }else if( target - sum >= 0 && min_diff == target - sum ){
      parts.insert(vp);
    }

    for(int pk=k-1;pk>=0;pk--){p[pk]++;if(p[pk]<=n-(k-pk)){for(int j=pk+1;j<k;j++)p[j]=p[j-1]+1;pk++;loop=true;break;}}
  }while(loop);
  delete [] p;
  return min_diff;
}

int solve(int target, string num, set< vector<string> > &parts){
  int len = num.length();
  int min = target + 1;
  for(int part = 1; part <= len; ++part){
    set< vector<string> > tmp_parts;
    int t = comb(len,part,target,num,tmp_parts);
    if( min > t ){
      min = t;
      parts = tmp_parts;
    }else if( min == t ){
      for(set< vector<string> >::iterator it = tmp_parts.begin(); it != tmp_parts.end(); ++it){
	parts.insert( *it );
      }
    }
  }
  return min;
}

int main()
{
  while(true){
    int target;
    string num;
    set< vector<string> > parts;

    cin >> target >> num;
    if( target == 0 && num == "0" ) break;
    int res = solve( target, num, parts );
    if( res == target + 1 ){
      cout << "error" << endl;
    }else if( parts.size() > 1 ){
      cout << "rejected" << endl;
    }else{
      cout << target - res;
      vector<string> p = *(parts.begin());
      for(int i = 0; i < (int)p.size(); ++i){
	cout << ' ' << p[i];
      }
      cout << endl;
    }
  }
  return 0;
}

