ll gcd(ll a, ll b) { return __gcd(a, b); }

ll euclid(ll a, ll b, ll &x, ll &y) {
    if (b) { ll d = euclid(b, a % b, y, x);
        return y -= a/b * x, d; }
    return x = 1, y = 0, a;
}

typedef unsigned long long ull;
typedef long double ld;
ull mod_mul(ull a, ull b, ull M) {
    ll ret = a * b - M * ull(ld(a) * ld(b) / ld(M));
    return ret + M * (ret < 0) - M * (ret >= (ll)M);
}
ull mod_pow(ull b, ull e, ull mod) {
    ull ans = 1;
    for (; e; b = mod_mul(b, b, mod), e /= 2)
        if (e & 1) ans = mod_mul(ans, b, mod);
    return ans;
}

const ll mod = 1000000007;
struct Mod {
    ll x;
    Mod(ll xx) : x(xx) {}
    Mod operator+(Mod b) { return Mod((x + b.x) % mod); }
    Mod operator-(Mod b) { return Mod((x - b.x) % mod); }
    Mod operator*(Mod b) { return Mod((x * b.x) % mod); }
    Mod operator/(Mod b) { return *this * invert(b); }
    Mod invert(Mod a) {
        ll x, y, g = euclid(a.x, mod, x, y);
        assert(g == 1); return Mod((x + mod) % mod);
    }
    Mod operator^(ll e) {
        if (!e) return Mod(1);
        Mod r = *this ^ (e / 2); r = r * r;
        return e & 1 ? *this * r : r;
    }
};
// endregion

void proc(int t, string num) {
    map<int, vector<vector<string>>> m;
    for (int i = 0; i < (1 << num.length() - 1); i++) {
        vector<string> parts;
        string buf;
        for (int j = 0; j < num.length(); j++) {
            buf += num[j];
            if (i & (1 << j)) {
                parts.push_back(buf); buf = "";
            }
        }
        if (buf.size()) parts.push_back(buf);
        // if (any_of(all(parts), [](string s) {return s.size() != 1 && s.front() == '0';})) {
        //     continue;
        // }
        int sum = 0;
        for (auto s : parts) sum += stoi(s);
        if (sum > t) continue;
        m[sum].push_back(parts);
    }
    if (m.empty()) {
        cout << "error" << endl;
    } else if (m.rbegin()->second.size() > 1) {
        cout << "rejected" << endl;
    } else {
        auto p = m.rbegin()->second.front();
        cout << m.rbegin()->first << " ";
        for (int i = 0; i < p.size(); i++) {
            cout << p[i] << " \n"[i + 1 == p.size()];
        }
    }
}

int main() {
    for (;;) {
        int t, num; cin >> t >> num;
        if (!t && !num) break;

        proc(t, to_string(num));
    }
}

