int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);

    while (true) {
        ll target;
        string num;
        cin >> target >> num;
        if (target == 0) break;

        ll N = SIZE(num);
        V<V<ll>> cut;

        FOR(b, 0, (1 << (N - 1)) - 1) {
            V<ll> v = {0};
            FOR(i, 0, N - 2) {
                if ((b >> i) & 1) v.pb(i + 1);
            }
            v.pb(N);

            V<ll> shred;
            FOR(i, 0, SIZE(v) - 2) {
                string p = num.substr(v[i], v[i + 1] - v[i]);
                shred.pb(stoi(p));
            }

            cut.pb(shred);
        }


        PQ<P<ll, V<ll>>> que;
        for (auto v : cut) {
            ll sum = 0;
            for (auto c : v) {
                sum += c;
            }

            if (sum <= target) {
                que.push(mp(sum, v));
            }
        }

        if (que.empty()) {
            cout << "error" << endl;
            continue;
        }

        auto p = que.top();
        que.pop();
        if (!que.empty() && p.fst == que.top().fst) {
